targets.expr:
histogram_quantile(
  0.95,
  sum by (le, job) (
    rate(
      http_server_request_duration_seconds_bucket{        
        job="$job"
      }[$__rate_interval]
    )
  )
)

or 

histogram_quantile(
  0.95,
  sum by (le, job) (
    rate(
      http_server_duration_milliseconds_bucket{        
        job="$job"
      }[$__rate_interval]
    )
  )
)

(avg by (job) (
  rate(
    http_server_request_duration_seconds_sum{
      job="$job"
    }[$__rate_interval]
  )
)
/
avg by (job) (
